  #{status,jdbcType=INTEGER}, #{creater,jdbcType=VARCHAR}, #{created,jdbcType=TIMESTAMP},
  #{modifier,jdbcType=VARCHAR}, #{modified,jdbcType=TIMESTAMP}, #{note,jdbcType=VARCHAR}
  )
